import time
import argparse
from naoqi import ALProxy
from pprint import pprint

def main(robotIP, PORT=9559):
    """ Simple code to test above motion data. """
    # Choregraphe simplified export in Python.
    motion  = ALProxy("ALMotion", robotIP, PORT)
    posture = ALProxy("ALRobotPosture", robotIP, PORT)
	# testList = upArms
    # testList = leftArmMovementList
	# testList = SixArmMovementList
	# testList = TenArmMovementList
    testList = SixArmMovementListB
	# testList = TenArmMovementList


    ttsProxy = ALProxy("ALTextToSpeech", robotIP, PORT)
    ttsProxy.say("Hiya, I am ROCY")
    ttsProxy.say("     ")
    ttsProxy.say("You are going to imitate the following arm movements")
    ttsProxy.say("     ")
    ttsProxy.say("Let's start")

    bezier = True

    motion.wakeUp()
    # posture.goToPosture("StandInit", 0.8)


    for i in range(len(testList)):
        names = list()
        times = list()
        keys = list()

        for n, t, k in testList[i]:
            names.append(n)
            times.append(t)
            keys.append(k)

        if bezier:
            motion.angleInterpolationBezier(names, times, keys)
        else:
            motion.angleInterpolation(names, keys, times, True)


    # Go to rest position

    motion.rest()
	# time.sleep(1)

    ttsProxy.say("     ")
    ttsProxy.say("Wow, Thank you very much")
    ttsProxy.say("     ")



if __name__ == "__main__":
    parser = argparse.ArgumentParser()
    parser.add_argument("--ip", type=str, default="169.254.199.42", help="Robot ip address")
    parser.add_argument("--port", type=int, default=9559, help="Robot port number")
    args = parser.parse_args()
    main(args.ip, args.port)
